[{"fieldname": "print_heading_template", "fieldtype": "Custom HTML", "options": "<h1></h1>"}, {"fieldtype": "Section Break", "label": ""}, {"fieldtype": "Column Break"}, {"fieldname": "_custom_html", "print_hide": 0, "label": "Custom HTML", "fieldtype": "HTML", "options": "<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <style>
        body {
            font-family: Arial, sans-serif;
            margin: 0;
            padding: 0;
            color: #333;
        }
        .receipt-container {
            width: 100%;
            max-width: 700px;
            margin: 20px auto;
            padding: 20px;
            border: 1px solid #ddd;
            border-radius: 8px;
            background-color: #f9f9f9;
        }
        .receipt-header {
            text-align: center;
            margin-bottom: 20px;
        }
        .receipt-header h1 {
            font-size: 24px;
            margin: 0;
        }
        .receipt-details {
            margin-bottom: 20px;
        }
        .details-row {
            display: flex;
            justify-content: space-between;
            margin-bottom: 10px;
        }
        .details-row div {
            width: 48%;
        }
        .footer {
            text-align: center;
            margin-top: 20px;
        }
        .footer p {
            font-size: 12px;
            color: #666;
        }
    </style>
</head>
<body>

<div class="receipt-container">
    <div class="receipt-header">
        <h1>Lease Contract Receipt</h1>
        <p>Contract ID: {{ doc.name }}</p>
    </div>

    <div class="receipt-details">
        <div class="details-row">
            <div>
                <strong>Tenant:</strong> {{ doc.tenant }}
            </div>
            <div>
                <strong>Shop:</strong> {{ doc.shop }}
            </div>
        </div>
        <div class="details-row">
            <div>
                <strong>Contract Start Date:</strong> {{ frappe.utils.formatdate(doc.start_date) }}
            </div>
            <div>
                <strong>Contract Expiry Date:</strong> {{ frappe.utils.formatdate(doc.end_date) }}
            </div>
        </div>
        <div class="details-row">
            <div>
                <strong>Payment Status:</strong> <span style="color: green;">{{ doc.payment_status }}</span>
            </div>
            <div>
                <strong>Rent Amount:</strong> {{ doc.rent_amount }} {{ doc.currency or "Sh" }}
            </div>
        </div>
    </div>

    <div class="footer">
        <p>Thank you for your payment!</p>
        <p>This is a system-generated receipt and does not require a signature.</p>
    </div>
</div>

</body>
</html>
